import React, { Component } from 'react';
import Link from 'next/link';
import logo from '../static/logo.png';
import search from '../static/search.png';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, CardText, CardHeader, CardBody, Button } from 'reactstrap';
import './HeaderStyle/Header.css';
//import openSlideMenu from './openSlideMenu';
//import closeSlideMenu from './closeSlideMenu';
import $ from 'jquery';
import Popper from 'popper.js';

class Header extends Component{

    render(){
        return(

            <div >
                <nav className = 'navbar'>
                    <div className = 'container-fluid'>
                    <SideMenu/>
                    <Burger/>
                    <SearchBox/>
                    <Menu/>
                    <Logo/>
                    </div>
                </nav>

                <style jsx>{`
                    .navbar{
                        background: #82A3A1;
                    }
                `}</style>
            </div>
        );
    }
}

const Logo = props => (
    <div className = ''>
        <Link href = '#'>
            <a className = 'navbar-brand'>
                        <img src={logo} alt = 'logo'/>
            </a>
        </Link>


    </div>
);

const MenuButton = props =>(
    <Link href = {`${props.href}`}>
        <li className = 'nav-item'><a className = 'nav-link'>{`${props.name}`}</a></li>


    </Link>
);

const Menu = props =>(
    <ul className = 'nav'>
        <MenuButton name = 'Main' href = '#'/>
        <MenuButton name = 'Songs' href = '#'/>
        <MenuButton name = 'Compositors' href = '#'/>
        <MenuButton name = 'Chords' href = '#'/>
        <MenuButton name = 'Articles' href = '#'/>

    </ul>


);

const SearchBox = props =>(
    <div className="col-md-3 col-md-offset-3">
        <div id = 'group' className="input-group"> 
            <input type="text" className="form-control" name="x" placeholder="Search"/>
            <span className="input-group-btn">
                <button className = 'searchButton' type="button"><span className="glyphicon glyphicon-search"></span><img  src={search} alt = 'search'/></button>
            </span>
        </div> 
    </div>

);

const Burger = props =>(
    <span className ="open-slide">
        <a href="#" onClick = {openSlideMenu()}>
            <svg width = "30" height = "30">
                <path d = "M0, 5, 30, 5" stroke = "#fff" strokeWidth="5"/>
                <path d = "M0, 14, 30, 14" stroke = "#fff" strokeWidth="5"/>
                <path d = "M0, 23, 30, 23" stroke = "#fff" strokeWidth="5"/>
            </svg>
        </a>
    </span>

);

const SideMenu = props =>(
    <div id = "side-menu" className = "side-nav">
        <a href="#" className = "btn-close" onClick = "closeSlideMenu()">&times;</a>
        <a href="Songs.html"> Songs</a> 
        <a href="Compositors.html"> Compositors</a> 
        <a href="#"> Chords</a>
        <a href="#"> Wiki</a>
    </div>
);

openSlideMenu = e => {
        e.preventDefault();
        const {name, value} = e.target;
        let formErrors = this.state.formErrors;

        switch(name) {
            case 'Email':
                formErrors.firstName = 
                    emeilRegex.test(value) && value.length > 0 
                        ?""
                        :'invalid email';
                break;
            case 'Password':
                    formErrors.firstName = 
                        value < 6 && value.length > 0 
                            ?'minimum 6 characters'
                            : "";
                break;
        }
        this.setState({formErrors, [name]: value}, () => console.log());
    }



export default Header;
