    }
    render(){
        return(
            <div>
                <div>
                    <Button id = 'Songs' divclassname = 'check' buttonclassname = 'checkButton' type = 'button' name = 'Songs' onClick = {this.handleClick}/>
                    <Button id = 'Compositors' divclassname = 'check' buttonclassname = 'checkButton' type = 'button' name = 'Compositors' onClick = {this.handleClick}/>
                    <Button id = 'Articles' divclassname = 'check' buttonclassname = 'checkButton' type = 'button' name = 'Articles' onClick = {this.handleClick}/>
