import React, { Component } from 'react'
import Link from 'next/link';
import { useRouter } from 'next/router';
import '../../Styles/AddSong.css';
import { FormText } from 'reactstrap';
import Axios from 'axios';
import EditText from './EditText/editText';

class AddSong extends Component{
    constructor(props){
        super(props)
        this.state = {
            cname: null,
            sname: null,
            capo: null,
            text: null,
            genre: null
        }
    } 

    handleSubmit = e => {
        e.preventDefault();
        console.log(this.state.text)
        console.log(this.state.cname)
    };

    handleChange = e => {
        e.preventDefault();
        const {name, value} = e.target;
        switch(name) {
            case 'cname':
                this.setState({cname: value});
                break;
            case 'sname':
                this.setState({sname: value});
                break;
            case 'capo':
                this.setState({capo: value});
            break;
            case 'genre':
                this.setState({genre: value});
            break;
            case 'text':
                this.setState({text: value});
            break;

        }
        console.log(this.state.value);
    }

    componentDidMount(){
       // Axios.get('http://localhost:3210/allSongs').then ((response) => { this.setState({songs: response.data})});
    }
    render(){
        return(

            <div className = "whiteBlock">
                <form onSubmit = {this.handleSubmit}>
                    <LineInput onChange = {this.onChange} type = 'compname' placeholder = 'Name of compositor' name = 'cname'/>
                    <LineInput onChange = {this.onChange} type = 'songname' placeholder = 'Name of song' name = 'sname'/>
                    <LineInput onChange = {this.onChange} type = 'capo' placeholder = 'Capo' name = 'capo'/>
                    <LineInput onChange = {this.onChange} type = 'genre' placeholder = 'Genre' name = 'genre'/>
                    <Button divclassname = 'SingIn' buttonclassname = 'SubmitButton' type = 'submit' name = 'submit'/>
                    <TextInput onChange = {this.onChange} classdiv = 'textblock' classtext = 'classtext' placeholder = "Song's Text" name = 'text'/>
                </form>
            </div>

        );
    }

}

const LineInput = props =>(
    <input 
    type = {`${props.type}`}
    className = 'FormInput' 
    placeholder = {`${props.placeholder}`}
    name = {`${props.name}`}
    onChange = {props.onChange}
    noValidate  
    />

);

const TextInput = props =>(
    <div className = {`${props.classdiv}`}>
        <textarea className = {`${props.classtext}`} name="text" placeholder = {`${props.placeholder}`} cols="100" rows="40"/>
    </div>
);

const Button = props => (
    <div className = {`${props.divclassname}`}>
      <button className = {`${props.buttonclassname}`} type = {`${props.type}`}>{props.name}</button>
    </div>
  )

export default AddSong;
