import React, {Component, createElement} from 'react';
import './SongStyle/SongStyle.css';
import Axios from 'axios';
import Link from 'next/link';
import ActiveLink from '../Properties/ActiveLink';

class SongList extends Component{
    constructor(props){
        super(props)
        this.state = {
          songs: [],
          comp: []
        }
    }     
    componentDidMount(){
        Axios.get('http://localhost:3210/allSongs').then ((response) => { this.setState({songs: response.data})});
    }
    render(){
        return(
            <ul>
                {this.state.songs.map((value) => {
                    return (

                        <SongRef
                        name = {value.name}
                        comp_id = {value.comp_id}
                        id = {value.id}
                        cname = {value.cname}
                        />
                    );
                })}
            </ul>
        );
    }



}


const SongRef = props =>(
    <ActiveLink href = '/song/' as ={`/song/${props.id}`}>
        <li><a>{props.cname} - {props.name}</a></li>
    </ActiveLink>
);

export default SongList;
