import React, {Component, createElement} from 'react';
import '../../Styles/SongStyle/SongStyle.css';
import Axios from 'axios';
import Link from 'next/link';


class ArticleList extends Component{
    constructor(props){
        super(props)
        this.state = {
          songs: []
        }
    }     
    componentDidMount(){
        Axios.get('http://localhost:3210/allArticles').then ((response) => { this.setState({songs: response.data})});
    }
    render(){
        return(
            <ul>
                {this.state.songs.map((value) => {
                    return (                       
                        <SongRef
                        id = {value.id}
                        name = {value.cname}
                        text = {value.text}
                        />
                    );

                })}
            </ul>
        );
    }



}


const SongRef = props =>(
    <Link href = '/Articles/[article]/' as ={`/Articles/${props.id}`}>
        <li><a>{props.name}</a></li>
    </Link>
);

export default ArticleList;
