import React, {Component, createElement} from 'react';
import '../../Styles/SongStyle/SongStyle.css';
import Axios from 'axios';
import Link from 'next/link';


class SearchResults extends Component{
    constructor(props){
        super(props)
        this.state = {
            resultsC: [],
            resultsS: [],
            resultsA: [],
            Compositors: false,
            Songs: false,
            Articles: false
        }
    }
    handleClick = e =>{
        //e.preventDefault();
        const {name, value} = e.target;
        console.log(typeof(name));
        console.log()
        if(this.state.name){
            e.target.style.backgroundColor = '#60f78a';
            this.setState({name: false})
            document.getElementById(name).style.display = 'none';
        }
        else{
            e.target.style.backgroundColor = '#8fffae';
            this.setState({name: true})
            document.getElementById(name).style.display = 'block';
        }
    }     
    componentDidMount(){

       // Axios.get('http://localhost:3210/searchC/').then ((response) => { this.setState({songs: resultsC.data})});
       //Axios.get('http://localhost:3210/searchS/').then ((response) => { this.setState({songs: resultsS.data})});
       //Axios.get('http://localhost:3210/searchA/').then ((response) => { this.setState({songs: resultsA.data})});
    }
    render(){
        return(
            <div>
                <div>
                    <Button  divclassname = 'check' buttonclassname = 'checkButton' type = 'button' name = 'Songs' onClick = {this.handleClick}/>
                    <Button  divclassname = 'check' buttonclassname = 'checkButton' type = 'button' name = 'Compositors' onClick = {this.handleClick}/>
                    <Button  divclassname = 'check' buttonclassname = 'checkButton' type = 'button' name = 'Articles' onClick = {this.handleClick}/>
                </div>

                <div id = 'Songs'>
                    Songs
                </div>
                <div id = 'Compositors'>
                Compositors
                </div>
                <div id = 'Articles'>
                    Articles
                </div>
            </div>
            // <ul>
            //     {this.state.songs.map((value) => {
            //         return (
            //             <ResRef
            //             name = {value.name}
            //             comp_id = {value.comp_id}
            //             id = {value.id}
            //             cname = {value.cname}
            //             />
            //         );
            //     })}
            // </ul>
        );
    }    
}



const ResRef = props =>(
    <Link href = '/SearchPages/[earch]/' as ={`/SearchPages/${props.id}`}>
        <li><a>{props.cname} - {props.name}</a></li>
    </Link>
);

const Button = props => (
    <div className = {`${props.divclassname}`}>
      <button name ={`${props.name}`} className = {`${props.buttonclassname}`} type = {`${props.type}`}  onClick = {props.onClick}>{props.name}</button>
    </div>
  )

export default SearchResults;
