import React, { Component } from 'react'
import '../../Styles/global.css'
import Slider from './Slider'
import '../../Styles/MainStyles/MainStyle.css'
import SongList from '../../components/Songs/SongList'

export default class Main extends Component{
    render(){
        return( 
            <div>
              <div className = 'slides'>
              <Slider/>
              </div>
              <div >

              <div className = 'LightDiv'>
                  <div><h3>Hi! I'm Vlad</h3></div>

                  <div>
                      I created this 'masterpiece' for two main reasons:<br/>
                      My coursework from Web and DB :)<br/>
                      I need some peacefull place to store songs chords<br/>
                      If somebody somewhere will gonna need this 'website' for some reason, you're welcome!
                      Here's songs that we have now:
                  </div>

              </div>
                  <style jsx>{`
                          div{
                              margin-left: auto;
                              margin-right: auto;
                              width: 1000px;
                              text-align: center;
                          }

                  `}</style>
                </div>
                <SongList/>
            </div>
        );

    }
}

const slideImages = [
    'static/ex1.jpg',
    'static/ex2.jpg',
    'static/me.jpg'
  ];

  const properties = {
    duration: 5000,
    transitionDuration: 500,
    infinite: true,
    indicators: true,
    arrows: true,
    onChange: (oldIndex, newIndex) => {
      console.log(`slide transition from ${oldIndex} to ${newIndex}`);
    }
  }

  const Slideshow = () => {
      return (
        <div className="slide-container">
          <Slide {...properties}>
            <div className="each-slide">
              <div style={{'backgroundImage': `url(${slideImages[0]})`}}>
                <span>Slide 1</span>
              </div>
            </div>
            <div className="each-slide">
              <div style={{'backgroundImage': `url(${slideImages[1]})`}}>
                <span>Slide 2</span>
              </div>
            </div>
            <div className="each-slide">
              <div style={{'backgroundImage': `url(${slideImages[2]})`}}>
                <span>Slide 3</span>
              </div>
            </div>
          </Slide>
        </div>
      )
  }
