import React, {Component, createElement} from 'react';
import './SongStyle/SongStyle.css';
//import Page from './Page';
import Axios from 'axios';
//import AllTheGoodG from './AllTheGoodG';

class Song extends Component{
        constructor(){
          super();
          this.state = {
              children: []
          }
      }
      children = []

      AddText(props){
        children.push(<Child value = {props.value}/>);
      }


      GetProfiles() {
        const a = Axios.get('http://localhost:3210/profile').then ((response) => {
        this.props.AddText(response.data[0])
        });

      }
      render(){
        const children = [];
      return(
        <div>
            <SongHead compName = 'Billie Eilish' name = 'All The Good Girls Go to Hell' capo = '4th fret'/>
            <button  onClick = {this.GetProfiles}/>
            <div id = 'SongLyrics'><SongText/></div>
        </div>
      );
    }

}

const SongHead = props =>(
    <div>
        <span><h1>{props.compName}</h1>
        <h3>{props.name}</h3></span>
        <span><h6>Capo: {props.capo}</h6></span>
        <span>----------------------------</span>
    </div>
);

const SongText = props =>(
  <div className = 'song'>
    {props.children}
  </div>
)

const Child = props => (<div>{props.value}</div>);

export default Song;
